import React, { useState } from "react";
import ReactDOM from "react-dom";
import "./index.css"

const App = () =>{

    const [listvalue, updatedval] = useState("Learn react");
    const [arrinit, arrfinal] = useState("")
  
    const arrayofitems = [];


    const changing = (event) =>{
            updatedval(event.target.value)
            // arrayofitems.push(event.target.value)
            // console.log(arrayofitems)
    }

    const listOfItems = (e) =>{
        // console.log(e.target.value)
        // arrayofitems.push(e.target.value)
        // console.log(arrayofitems)
        arrfinal(arrinit);

    }

    return(
        <>
            <div className="main_div">
                <div className="center_div">
                    <br></br>
                    <h1> ToDo List</h1>
                    <br></br>
                    <input onChange={changing} type="text" placeholder="Add an item" value={listvalue}></input>
                    <button onClick={listOfItems}>+</button>

                    <ol >
                        <li >{listvalue}</li>
                        <li>{arrinit}</li>
                    </ol>
                </div>         
            </div>
        </>
    );

}

export default App;
