import React, { useState } from "react";
import ReactDOM from "react-dom";
import "./index.css"

const App = () =>{

    const arrayofitems = [];

    const [listvalue, updatedval] = useState("");
    const [arrinit, arrfinal] = useState([arrayofitems])
  
    const changing = (event) =>{
            updatedval(event.target.value)
    }

    const btnClk = () =>{
        // arrfinal(listvalue);
        // arrayofitems.push(listvalue);
        // console.log(arrayofitems);
        arrfinal( (modifiedarray)=>{
            return [...modifiedarray,listvalue]
        })
    }

    return(
        <>
            <div className="main_div">
                <div className="center_div">
                    <br></br>
                    <h1> ToDo List</h1>
                    <br></br>
                    <input onChange={changing} type="text" placeholder="Add an item" value={listvalue}></input>
                    <button onClick={btnClk}>+</button>

                    <ol >
                        {/* <li >{listvalue}</li> */}
                        {/* <li>{arrinit}</li> */}
                        {arrinit.map((itemvalue) =>{
                            return <li> {itemvalue} </li>
                        })}
                    </ol>
                </div>         
            </div>
        </>
    );

}

export default App;
