import React from "react";
import ReactDOM from "react-dom";
import "./index.css"

const App = () =>{

    const changing = (event) =>{
        // console.log(event.target.value)
        document.getElementsByClassName('text').innerHTML = event.target.value
    }

    return(
        <>
            <div className="main_div">
                <div className="center_div">
                    <br></br>
                    <h1> ToDo List</h1>
                    <br></br>
                    <input onChange={changing} type="text" placeholder="Add an item"></input>
                    <button>+</button>

                    <ol className="text">
                        <li>buy apple</li>
                    </ol>
                </div>         
            </div>
        </>
    );

}

export default App;
